insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (1,'zentaophp框架中的一个bug？需要将数组类型的返回值再转为数组','&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;禅道php做blog，新增界面的tag想用checkbox实现，使用lib/base/front中的html类，要求第二个参数是数组，调试发现 &amp;nbsp;$this-&amp;gt;view-&amp;gt;tags = $this-&amp;gt;blog-&amp;gt;getTagNames()返回的虽然是一个数据，但是数组元素是对象，只得手工将对象里的值转为数组：\r\n$tag_arr=array();\r\n foreach($tags as $key =&amp;gt; $value)\r\n {\r\n &amp;nbsp;$tag_arr[$i]= $value-&amp;gt;tag;\r\n &amp;nbsp;$i=$i+1;\r\n &amp;nbsp; }\r\n echo html::checkbox(&amp;#39;tag_id&amp;#39;, $tag_arr); \r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;','dev','2018-09-13 14:15:35','2018-11-09 15:17:38',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (2,'zentaoPHP保存到数据库时增加减少字段的方法','&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;使用lib/base/fixer类，里面有add 、remove方法，例如： &amp;nbsp; \r\n$article = fixer::input(&amp;#39;post&amp;#39;)-&amp;gt;specialchars(&amp;#39;title, content,cat_code,tag_id&amp;#39;)-&amp;gt;remove(&amp;#39;tag_id&amp;#39;)-&amp;gt;add(&amp;#39;date&amp;#39;, date(&amp;#39;Y-m-d H:i:s&amp;#39;))-&amp;gt;get();\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;','dev','2018-09-13 19:48:28','2018-11-09 15:18:27',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (3,'在禅道我的地盘里先增加bug的截止日期','为了重点关注bug截止日期到了以后还没有被解决的现象，首先在禅道我的地盘里先能看到bug的截止日期     \r\n在module/block/view/bugblock.html.php代码中加上了一列，用于显示bug的截止日期  \r\n&lt;td class=&#039;c-name&#039; style=&#039;color: &lt;?php echo $bug-&gt;color?&gt;&#039; title=&#039;&lt;?php echo $bug-&gt;deadline?&gt;&#039;&gt;&lt;?php echo $bug-&gt;deadline?&gt;&lt;/td&gt;\r\n     \r\n改在了85机器的新版本上，在block模块下增加了一个ext目录','dev','2018-09-15 19:02:06',NULL,1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (4,'做git与禅道的集成','&lt;p&gt;git模块的配置文件位于zentao/module/git/config.php,改后 &amp;nbsp; &lt;br/&gt;&lt;/p&gt;&lt;p&gt;$config-&amp;gt;git = new stdClass(); &lt;br/&gt;&lt;/p&gt;&lt;p&gt;$config-&amp;gt;git-&amp;gt;encodings = &amp;#39;utf-8&amp;#39;; &amp;nbsp; &lt;br/&gt;&lt;/p&gt;&lt;p&gt;$config-&amp;gt;git-&amp;gt;client &amp;nbsp; &amp;nbsp;= &amp;#39;/usr/bin/git&amp;#39;;\r\n$i = 1; &lt;br/&gt;&lt;/p&gt;&lt;p&gt;$config-&amp;gt;git-&amp;gt;repos[$i][&amp;#39;path&amp;#39;] &amp;nbsp; &amp;nbsp; = &amp;#39;/home/kunlun/rxsundocs/tttt&amp;#39;; &lt;br/&gt;&lt;/p&gt;&lt;p&gt;$config-&amp;gt;git-&amp;gt;repos[$i][&amp;#39;encoding&amp;#39;] = &amp;#39;utf-8&amp;#39;; &lt;br/&gt;&lt;/p&gt;&lt;p&gt;http://www.zentao.net/book/zentaopmshelp/35.html 按照这个来初始化禅道的命令工具 &amp;nbsp; &lt;br/&gt;&lt;/p&gt;&lt;p&gt;cd zentao/bin &amp;nbsp;.执行/init.sh &lt;br/&gt;&lt;/p&gt;&lt;p&gt;php位置是/usr/bin/php5.6 &lt;br/&gt;&lt;/p&gt;&lt;p&gt;root@kunlunsec:/var/www/zentaopms/bin# ./init.sh &lt;br/&gt;&lt;/p&gt;&lt;p&gt;Please input your php path:(example: /usr/bin/php)\r\n/usr/bin/php5.6 &lt;br/&gt;&lt;/p&gt;&lt;p&gt;Please input zentao url:(example: http://localhost:88/zentao or &lt;a href=&quot;http://localhost)\r\nhttp://192.168.200.84:85\r\n&quot;&gt;http://localhost)\r\nhttp://192.168.200.84:85&lt;/a&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;ztcli.sh ok &amp;nbsp; &amp;nbsp; &lt;br/&gt;&lt;/p&gt;&lt;p&gt;backup.sh ok &lt;br/&gt;&lt;/p&gt;&lt;p&gt;computeburn.sh ok &lt;br/&gt;&lt;/p&gt;&lt;p&gt;dailyreminder.sh ok &lt;br/&gt;&lt;/p&gt;&lt;p&gt;checkdb.sh ok &lt;br/&gt;&lt;/p&gt;&lt;p&gt;syncsvn.sh ok &lt;br/&gt;&lt;/p&gt;&lt;p&gt;syncgit.sh ok &lt;br/&gt;&lt;/p&gt;&lt;p&gt;ron.sh ok&lt;/p&gt;&lt;p&gt;操作/home/kunlun/rxsundocs/tttt库，改动代码，commit日志里要有bug#035等字样。 &lt;br/&gt;&lt;/p&gt;&lt;p&gt;执行./syncgit.sh 在该bug的历史记录里可以看到某开发人员做的修改 &lt;br/&gt;&lt;/p&gt;&lt;p&gt;可以再做一下自动定时执行 &lt;br/&gt;&lt;/p&gt;&lt;p&gt;以上参考了 http://www.zentao.net/book/zentaopmshelp/207.html\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;','tool','2018-09-15 20:57:07','2018-11-09 15:21:42',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (5,'在84机上改phpmyadmin连接远程数据库','&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;修改/var/www/phpmyadmin目录中libraries文件夹下的config.default.php文件，&lt;/p&gt;&lt;p&gt;先将原文件另存 &lt;br/&gt;&lt;/p&gt;&lt;p&gt;$cfg[&amp;#39;AllowArbitraryServer&amp;#39;] = false;修改成：$cfg[&amp;#39;AllowArbitraryServer&amp;#39;] = true; &lt;br/&gt;&lt;/p&gt;&lt;p&gt;将$cfg[&amp;#39;Servers&amp;#39;][$i][&amp;#39;host&amp;#39;] = &amp;#39;&amp;#39;;改为你服务器的ip地址 &lt;br/&gt;&lt;/p&gt;&lt;p&gt;将$cfg[&amp;#39;Servers&amp;#39;][$i][&amp;#39;port&amp;#39;] = &amp;#39;&amp;#39;;改为你服务器的端口 &lt;br/&gt;&lt;/p&gt;&lt;p&gt;将$cfg[&amp;#39;Servers&amp;#39;][$i][&amp;#39;user&amp;#39;] = &amp;#39;root&amp;#39;;改为你服务器的用户名&lt;/p&gt;&lt;p&gt;将$cfg[&amp;#39;Servers&amp;#39;][$i][&amp;#39;password&amp;#39;] = &amp;#39;&amp;#39;;改为你服务器的密码 &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;','service','2018-09-17 11:25:55','2018-11-09 15:19:23',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (6,'禅道与git的集成及使用说明---总结','&lt;p&gt;1.原理\r\n\r\n通过git客户端软件的log命令，来获得所有的提交记录，然后分析，得出相应的id，写入数据库。\r\n\r\n目前禅道只能集成禅道所在的机器上的git仓库 &lt;br/&gt;&lt;/p&gt;&lt;p&gt;2.改git模块的配置文件\r\n\r\n位于zentao/module/git/config.php，备份一份后修改为：\r\n\r\n$config-&amp;gt;git = new stdClass();\r\n\r\n$config-&amp;gt;git-&amp;gt;encodings = &amp;#39;utf-8&amp;#39;;\r\n\r\n$config-&amp;gt;git-&amp;gt;client = &amp;#39;/usr/bin/git&amp;#39;;\r\n\r\n$i = 1;\r\n\r\n$config-&amp;gt;git-&amp;gt;repos[$i][&amp;#39;path&amp;#39;] = &amp;#39;/home/kunlun/rxsundocs/tttt&amp;#39;;\r\n$config-&amp;gt;git-&amp;gt;repos[$i][&amp;#39;encoding&amp;#39;] = &amp;#39;utf-8&amp;#39;;\r\n\r\n可以配置多个git库 &lt;br/&gt;&lt;/p&gt;&lt;p&gt;3.代码提交&amp;nbsp;\r\n开发者在提交代码到git的时候，需要在commit里面注明此次修改相关的需求，或者任务，或者bug的id。比如下面的格式：\r\nbug#123,234, 1234，也可以是bug:123,234 1234，id列表之间，用逗号和空格都可以。\r\nstory#123 task#123\r\nbug, story, task是必须标注的。 &lt;br/&gt;&lt;/p&gt;&lt;p&gt;4.安装初始化脚本\r\ncd zentao/bin sudo ./init.sh 提示输入php位置 是/usr/bin/php5.6，输入禅道url，是&lt;a href=&quot;http://192.168.200.84:85\r\n会安装一些自动化脚本，包括了git同步的脚本。\r\n&quot;&gt;http://192.168.200.84:85\r\n会安装一些自动化脚本，包括了git同步的脚本。&lt;/a&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;5.手工执行./syncgit.sh ，就会同步git的log记录到禅道数据库里，到该bug的历史记录里可以看到某开发人员做的修改，点view diff 可以看到修改的行数 &lt;br/&gt;&lt;/p&gt;&lt;p&gt;6.做自动定时同步\r\n将syncgit.sh 脚本每5分钟执行一次，就可以看到每一个bug在代码中的修改内容，一个bug的多次修改都可一目了然。\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;','tool','2018-09-17 11:30:31','2018-11-09 15:23:40',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (7,'fieldset 与legend 的组合','&lt;p&gt;fieldset 与legend 的组合是比较漂亮的&lt;br/&gt;&amp;nbsp;&amp;lt;form&amp;gt;&lt;br/&gt;&amp;nbsp; &amp;lt;fieldset&amp;gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;lt;legend&amp;gt;健康信息&amp;lt;/legend&amp;gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; 身高：&amp;lt;input type=&amp;quot;text&amp;quot; /&amp;gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; 体重：&amp;lt;input type=&amp;quot;text&amp;quot; /&amp;gt;&lt;br/&gt;&amp;nbsp; &amp;lt;/fieldset&amp;gt;&lt;br/&gt;&amp;nbsp;&amp;lt;/form&amp;gt;&lt;/p&gt;&lt;p&gt;legend是边框上的标题 &lt;br/&gt;&lt;/p&gt;','dev','2018-09-17 11:31:43',NULL,1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (8,'thinkcmf模板学习','&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;__TMPL__是模板根目录；\r\nis_public 1 是公共的 其它模板都可以访问这个模板\r\norder 建议10个间隔 以后插入\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;','dev','2018-09-17 11:34:57',NULL,1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (9,'为装新代码库系统准备工具','&lt;p&gt;下载ubuntu16.04 因为18.04 不能识别 做安装u盘 &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&lt;/p&gt;&lt;p&gt;装docker pull 镜像 调内网邮件 能发不能点 缺少端口号81 还没有改过来&lt;br/&gt;&lt;/p&gt;','service','2018-09-17 13:58:01','2018-11-09 15:25:46',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (10,'volist和foreach标签学习','&lt;h2&gt;volist标签&lt;/h2&gt;&lt;table&gt;&lt;thead&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;th&gt;标签名&lt;/th&gt;&lt;th&gt;作用&lt;/th&gt;&lt;th&gt;包含属性&lt;/th&gt;&lt;/tr&gt;&lt;/thead&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;volist&lt;/td&gt;&lt;td&gt;数组数据循环输出&lt;/td&gt;&lt;td&gt;name,id,offset,length,key,mod&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;标签属性:&lt;/p&gt;&lt;table&gt;&lt;thead&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;th&gt;标签属性名&lt;/th&gt;&lt;th&gt;含义&lt;/th&gt;&lt;/tr&gt;&lt;/thead&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;name&lt;/td&gt;&lt;td&gt;模板赋值的变量名称,必须&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;id&lt;/td&gt;&lt;td&gt;当前的循环变量名称,必须&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;offset&lt;/td&gt;&lt;td&gt;循环开始索引,默认从0开始&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;length&lt;/td&gt;&lt;td&gt;循环长度,默认为 null&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;key&lt;/td&gt;&lt;td&gt;当前的循环的下标变量名,默认为i&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;mod&lt;/td&gt;&lt;td&gt;key取余,默认为2&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;循环输出用户的编号和姓名：&lt;/p&gt;&lt;pre&gt;&amp;lt;volist&amp;nbsp;name=&amp;quot;users&amp;quot;&amp;nbsp;id=&amp;quot;vo&amp;quot;&amp;gt;{$vo.id}&amp;nbsp;&amp;nbsp;{$vo.username}&amp;lt;br/&amp;gt;&amp;lt;/volist&amp;gt;&lt;/pre&gt;&lt;p&gt;volist标签的name属性表示模板赋值的变量名称,因此不可随意在模板文件中改变。 id表示当前的循环变量名称，可以随意指定，但不要和name属性冲突,如：&lt;/p&gt;&lt;pre&gt;&amp;lt;volist&amp;nbsp;name=&amp;quot;users&amp;quot;&amp;nbsp;id=&amp;quot;item&amp;quot;&amp;gt;{$item.id}&amp;nbsp;&amp;nbsp;{$item.username}&amp;lt;br/&amp;gt;&amp;lt;/volist&amp;gt;&lt;/pre&gt;&lt;p&gt;输出查询结果中的部分数据,如:输出其中的第1-5条记录&lt;/p&gt;&lt;pre&gt;&amp;lt;volist&amp;nbsp;name=&amp;quot;users&amp;quot;&amp;nbsp;id=&amp;quot;vo&amp;quot;&amp;nbsp;offset=&amp;quot;0&amp;quot;&amp;nbsp;length=&amp;quot;5&amp;quot;&amp;gt;{$vo.id}&amp;nbsp;{$vo.username}&amp;lt;/volist&amp;gt;&lt;/pre&gt;&lt;p&gt;输出偶数记录&lt;/p&gt;&lt;pre&gt;&amp;lt;volist&amp;nbsp;name=&amp;quot;users&amp;quot;&amp;nbsp;id=&amp;quot;vo&amp;quot;&amp;nbsp;mod=&amp;quot;2&amp;quot;&amp;nbsp;&amp;gt;&amp;lt;eq&amp;nbsp;name=&amp;quot;mod&amp;quot;&amp;nbsp;value=&amp;quot;1&amp;quot;&amp;gt;{$vo.id}&amp;nbsp;{$vo.username}&amp;lt;/eq&amp;gt;&amp;lt;/volist&amp;gt;&lt;/pre&gt;&lt;p&gt;mod属性还用于控制一定记录的换行,如：&lt;/p&gt;&lt;pre&gt;&amp;lt;volist&amp;nbsp;name=&amp;quot;users&amp;quot;&amp;nbsp;id=&amp;quot;vo&amp;quot;&amp;nbsp;mod=&amp;quot;4&amp;quot;&amp;gt;{$vo.id}&amp;nbsp;{$vo.username}&amp;lt;eq&amp;nbsp;name=&amp;quot;mod&amp;quot;&amp;nbsp;value=&amp;quot;3&amp;quot;&amp;gt;&amp;lt;br/&amp;gt;&amp;lt;/eq&amp;gt;&amp;lt;/volist&amp;gt;&lt;/pre&gt;&lt;p&gt;输出循环下标：&lt;/p&gt;&lt;pre&gt;&amp;lt;volist&amp;nbsp;name=&amp;quot;users&amp;quot;&amp;nbsp;id=&amp;quot;vo&amp;quot;&amp;nbsp;key=&amp;quot;k&amp;quot;&amp;gt;{$k}.&amp;nbsp;{$vo.id}&amp;nbsp;{$vo.username}&amp;lt;/volist&amp;gt;&lt;/pre&gt;&lt;p&gt;如果没有指定key属性的话，默认使用循环变量i,如：&lt;/p&gt;&lt;pre&gt;&amp;lt;volist&amp;nbsp;name=&amp;quot;users&amp;quot;&amp;nbsp;id=&amp;quot;vo&amp;quot;&amp;gt;{$i}.&amp;nbsp;{$vo.id}&amp;nbsp;{$vo.username}&amp;lt;/volist&amp;gt;&lt;/pre&gt;&lt;p&gt;如果要输出数组的索引,可以直接使用key变量,和循环变量不同的是,这个key是由数据本身决定，而不是循环控制的,如：&lt;/p&gt;&lt;pre&gt;&amp;lt;volist&amp;nbsp;name=&amp;quot;users&amp;quot;&amp;nbsp;id=&amp;quot;vo&amp;quot;&amp;gt;{$key}.&amp;nbsp;{$vo.id}&amp;nbsp;{$vo.username}&amp;lt;/volist&amp;gt;&lt;/pre&gt;&lt;p&gt;模板中可以直接使用函数设定数据集，而不需要在控制器中给模板变量赋值传入数据集变量，如：&lt;/p&gt;&lt;pre&gt;&amp;lt;volist&amp;nbsp;name=&amp;quot;:function_name(&amp;#39;arg0&amp;#39;)&amp;quot;&amp;nbsp;id=&amp;quot;vo&amp;quot;&amp;gt;{$vo.id}&amp;nbsp;{$vo.username}&amp;lt;/volist&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;h2&gt;&lt;span class=&quot;highlight&quot;&gt;foreach&lt;/span&gt;标签&lt;/h2&gt;&lt;p&gt;&lt;span class=&quot;highlight&quot;&gt;foreach&lt;/span&gt;标签和 volist 标签类似&lt;/p&gt;&lt;table&gt;&lt;thead&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;th&gt;标签名&lt;/th&gt;&lt;th&gt;作用&lt;/th&gt;&lt;th&gt;包含属性&lt;/th&gt;&lt;/tr&gt;&lt;/thead&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;&lt;span class=&quot;highlight&quot;&gt;foreach&lt;/span&gt;&lt;/td&gt;&lt;td&gt;数组数据循环输出&lt;/td&gt;&lt;td&gt;name,id或 item,offset,length,key,index,mod&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;标签属性:&lt;/p&gt;&lt;table&gt;&lt;thead&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;th&gt;标签属性名&lt;/th&gt;&lt;th&gt;含义&lt;/th&gt;&lt;/tr&gt;&lt;/thead&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;name&lt;/td&gt;&lt;td&gt;模板赋值的变量名称,必须&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;item或 id&lt;/td&gt;&lt;td&gt;当前的循环变量名称,必须&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;offset&lt;/td&gt;&lt;td&gt;循环开始索引,默认从0开始&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;length&lt;/td&gt;&lt;td&gt;循环长度,默认为 null&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;key&lt;/td&gt;&lt;td&gt;索引的变量名, 默认为 key&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;循环输出用户的编号和姓名：&lt;/p&gt;&lt;pre&gt;&amp;lt;foreach&amp;nbsp;name=&amp;quot;users&amp;quot;&amp;nbsp;item=&amp;quot;vo&amp;quot;&amp;gt;{$vo.id}&amp;nbsp;&amp;nbsp;{$vo.username}&amp;lt;br/&amp;gt;&amp;lt;/foreach&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;不同之处：\r\nvolist的key是当前的循环的下标变量名 默认是i\r\nforeach的key是数组的键值\r\n\r\n总结：无键值用volist 有键值用foreach foreach 是php语法\r\n\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;','dev','2018-09-17 14:04:10','2018-11-09 15:31:48',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (11,'foreach--php语法','&lt;p&gt;在PHP中经常会用到foreach的使用，而要用到foreach，就必须用到数组。因此，在这篇文章中，我们一边讲数组，一边讲foreach。&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;foreach有两种语法：&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;第一种：遍历给定的 数组语句 array_expression 数组。每次循环中，当前单元的值被赋给 $value 并且数组内部的指针向前移一步（因此下一次循环中将会得到下一个单元）。&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;foreach&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;(array_expression &lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;as&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;)&amp;nbsp; &lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;第二种：同上，同时当前单元的键名也会在每次循环中被赋给变量 $key。&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;foreach&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;(array_expression &lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;as&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$key&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;)&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;下边我们一一来讲解一下！&lt;/p&gt;&lt;p&gt;&lt;strong&gt;一、一维普通数组 与 foreach&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;我们先写一个一维数组，如下：&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$a&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Tom&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Mary&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Peter&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Jack&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;1、我们用第一种foreach方法来输出。&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;foreach&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$a&lt;/code&gt; &lt;code class=&quot;php keyword&quot;&gt;as&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;) {&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;.&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;&amp;lt;br/&amp;gt;&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;}&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;最后得到的结果是：&lt;/p&gt;&lt;p&gt;Tom&lt;br/&gt;Mary&lt;br/&gt;Peter&lt;br/&gt;Jack&lt;br/&gt;&lt;/p&gt;&lt;p&gt;2、我们用第二种 foreach 方法来输出。&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;foreach&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$a&lt;/code&gt; &lt;code class=&quot;php keyword&quot;&gt;as&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$key&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;) {&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$key&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;.&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;,&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;.&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;.&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;&amp;lt;br/&amp;gt;&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;}&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;最后得到的结果是：&lt;/p&gt;&lt;p&gt;0,Tom&lt;br/&gt;1,Mary&lt;br/&gt;2,Peter&lt;br/&gt;3,Jack&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;总结： &lt;/strong&gt;很明显，我们看到只是多了一个 $key ， 而这个 $key 的值就是序号 1、2、3、4 等等！&lt;/p&gt;&lt;p&gt;&lt;strong&gt;二、一维关联数组 与 foreach&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;一维关联数组如下：&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$b&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;a&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Tom&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;b&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Mary&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;c&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Peter&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;d&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Jack&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;也有人爱这么写，如下：&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$b&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;a&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Tom&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;b&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Mary&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;c&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Peter&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;d&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Jack&amp;#39;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;);&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;1、我们用第一种foreach方法来输出和上边一样。&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;foreach&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$b&lt;/code&gt; &lt;code class=&quot;php keyword&quot;&gt;as&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;) {&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;.&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;&amp;lt;br/&amp;gt;&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;}&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;最后得到的结果是：&lt;/p&gt;&lt;p&gt;Tom&lt;br/&gt;Mary&lt;br/&gt;Peter&lt;br/&gt;Jack&lt;br/&gt;&lt;/p&gt;&lt;p&gt;2、我们用第二种foreach方法来输出。&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;foreach&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$b&lt;/code&gt; &lt;code class=&quot;php keyword&quot;&gt;as&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$key&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;) {&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$key&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;.&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;,&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;.&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;.&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;&amp;lt;br/&amp;gt;&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;}&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;最后得到的结果是：&lt;/p&gt;&lt;p&gt;a,Tom&lt;br/&gt;b,Mary&lt;br/&gt;c,Peter&lt;br/&gt;d,Jack&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;总结：&lt;/strong&gt; 很明显，在一维关联数组下， $key就是关联的序号，即相应的a、b、c、d。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;三、二维普通数组 与 foreach&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;遍历二维数组时，稍微麻烦一些，为什么呢？因为遍历出来的value值，是一个数组，既然是数组，那么就可以对数组进行各种操作！&lt;/p&gt;&lt;p&gt;我们先看一个基本的二维数组，如下：&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$c&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;1&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Tom&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;),&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;2&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Mary&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;),&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;3&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Peter&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;),&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;4&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Jack&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;)&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;);&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;1、我们采用第一种 foreach方法：&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;foreach&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$c&lt;/code&gt; &lt;code class=&quot;php keyword&quot;&gt;as&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;) {&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;print_r(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt; &lt;code class=&quot;php string&quot;&gt;&amp;quot;&amp;lt;br/&amp;gt;&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;}&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;得到这样的结果：&lt;/p&gt;&lt;p&gt;Array ( [0] =&amp;gt; 11 [1] =&amp;gt; Tom )&lt;br/&gt;Array ( [0] =&amp;gt; 22 [1] =&amp;gt; Mary )&lt;br/&gt;Array ( [0] =&amp;gt; 33 [1] =&amp;gt; Peter )&lt;br/&gt;Array ( [0] =&amp;gt; 44 [1] =&amp;gt; Jack )&lt;br/&gt;&lt;/p&gt;&lt;p&gt;2、我们采用第二种foreach方法：&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;foreach&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$c&lt;/code&gt; &lt;code class=&quot;php keyword&quot;&gt;as&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$key&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;) {&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt; &lt;code class=&quot;php string&quot;&gt;&amp;#39;$key=&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;.&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$key&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;.&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;&amp;lt;br/&amp;gt;&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;print_r(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt; &lt;code class=&quot;php string&quot;&gt;&amp;quot;&amp;lt;br/&amp;gt;&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;}&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;得到如下的结果:&lt;/p&gt;&lt;p&gt;$key=0&lt;br/&gt;Array ( [0] =&amp;gt; 11 [1] =&amp;gt; Tom )&lt;br/&gt;$key=1&lt;br/&gt;Array ( [0] =&amp;gt; 22 [1] =&amp;gt; Mary )&lt;br/&gt;$key=2&lt;br/&gt;Array ( [0] =&amp;gt; 33 [1] =&amp;gt; Peter )&lt;br/&gt;$key=3&lt;br/&gt;Array ( [0] =&amp;gt; 44 [1] =&amp;gt; Jack )&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;总结：&lt;/strong&gt;由上可知，基本二维数组，$key就是序号 ,如 0\\1\\2\\3 等等！&lt;/p&gt;&lt;p&gt;&lt;strong&gt;四、关联二维数组 与 foreach&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;说明，关联二维数组在实际项目中，用到的可多了。为什么？一般数据库提取出来的数据都是关联二维数组，学会了关联二维数组，就在PHP的实战中，已经掌握了很大一部分了！&lt;/p&gt;&lt;p&gt;那么先列出关联二维数组，如下：&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$d&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;id&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;11&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Tom&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;),&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;id&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;22&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Mary&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;),&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;id&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;33&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Peter&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;),&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;id&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;44&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Jack&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;)&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;);&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;1、用第一种方法代码：&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;foreach&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$d&lt;/code&gt; &lt;code class=&quot;php keyword&quot;&gt;as&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;) {&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;print_r(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt; &lt;code class=&quot;php string&quot;&gt;&amp;quot;&amp;lt;br/&amp;gt;&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;}&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;得到的结果如下：&lt;/p&gt;&lt;p&gt;Array ( [id] =&amp;gt; 11 [name] =&amp;gt; Tom )&lt;br/&gt;Array ( [id] =&amp;gt; 22 [name] =&amp;gt; Mary )&lt;br/&gt;Array ( [id] =&amp;gt; 33 [name] =&amp;gt; Peter )&lt;br/&gt;Array ( [id] =&amp;gt; 44 [name] =&amp;gt; Jack )&lt;br/&gt;&lt;/p&gt;&lt;p&gt;很明显，关联与不关联的区别就是：不关联前边是 0/1 等等，而关联显示具体名称 id/name 等等。&lt;/p&gt;&lt;p&gt;2、用第二种方法的代码：&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;foreach&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$d&lt;/code&gt; &lt;code class=&quot;php keyword&quot;&gt;as&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$key&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;) {&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt; &lt;code class=&quot;php string&quot;&gt;&amp;#39;$key=&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;.&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$key&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;.&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;quot;&amp;lt;br/&amp;gt;&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;print_r(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt; &lt;code class=&quot;php string&quot;&gt;&amp;quot;&amp;lt;br/&amp;gt;&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;}&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;得到的结果如下：&lt;/p&gt;&lt;p&gt;$key=0&lt;br/&gt;Array ( [id] =&amp;gt; 11 [name] =&amp;gt; Tom )&lt;br/&gt;$key=1&lt;br/&gt;Array ( [id] =&amp;gt; 22 [name] =&amp;gt; Mary )&lt;br/&gt;$key=2&lt;br/&gt;Array ( [id] =&amp;gt; 33 [name] =&amp;gt; Peter )&lt;br/&gt;$key=3&lt;br/&gt;Array ( [id] =&amp;gt; 44 [name] =&amp;gt; Jack )&lt;br/&gt;&lt;/p&gt;&lt;p&gt;总结：这里$key仍然是 0/1/2/3 。&lt;/p&gt;&lt;p&gt;&lt;strong&gt;五、项目中实战应用&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;说明：在项目中，数组的变化好多，当然foreach功不可没！当然也可以用while、each等等方法，但是foreach最为方便了！下边简要说一下几个常见的项目实战！&lt;/p&gt;&lt;p&gt;实战1：将二维关联数组变为一维普通数组&lt;/p&gt;&lt;p&gt;还是第四列出关联二维数组，如下：&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$d&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;id&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;11&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Tom&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;),&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;id&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;22&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Mary&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;),&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;id&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;33&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Peter&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;),&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;id&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;44&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;=&amp;gt;&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Jack&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;)&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;);&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;现在我们只要 name 一列的内容，当然我们可以用以下的方法来实现，&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;foreach&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$d&lt;/code&gt; &lt;code class=&quot;php keyword&quot;&gt;as&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$key&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;) {&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;]);&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php functions&quot;&gt;echo&lt;/code&gt; &lt;code class=&quot;php string&quot;&gt;&amp;quot;&amp;lt;br/&amp;gt;&amp;quot;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;}&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;但有时候我们不得不将之列为一个一维数组，于是我们就有了以下的方法：&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php comments&quot;&gt;//获取name列作为一维数组&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$nameArr&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(); &lt;/code&gt;&lt;code class=&quot;php comments&quot;&gt;//name列&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php keyword&quot;&gt;foreach&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$d&lt;/code&gt; &lt;code class=&quot;php keyword&quot;&gt;as&lt;/code&gt; &lt;code class=&quot;php variable&quot;&gt;$key&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;) {&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$nameArr&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[] = &lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$value&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;];&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;}&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;print_r(&lt;/code&gt;&lt;code class=&quot;php variable&quot;&gt;$nameArr&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;以上通过赋空数组值，foreach 这个空数组等于我们的值，就得到了一个新的数组！以上代码的结果如下：&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;7&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;Array&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[0] =&amp;gt; Tom&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[1] =&amp;gt; Mary&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[2] =&amp;gt; Peter&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;[3] =&amp;gt; Jack&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;)&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;这个数组明显是：一维普通数组，如下：&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$d&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Tom&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Mary&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Peter&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;Jack&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;);&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;好了，将二维关联数组变为一维普通数组就写到了这里！&lt;/p&gt;&lt;p&gt;&lt;strong&gt;实战2 ：二级分类以及无限级分类&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;很明显，我们从数据库中取出来的数据就是一个二维数组，而且是二维关联数组。那么，我们怎么取出父分类？怎么取出对应父分类的子分类呢？&lt;/p&gt;&lt;p&gt;首先要说明的是：几乎所有的分类都是一个数据库模式，因此我们十分有必要了解它的结构，还有怎么取出对应的数据！&lt;/p&gt;&lt;p&gt;对于二级分类，为了说明方便，我从网上找一个比较好说明的例子，那就是“新闻分类“！&lt;/p&gt;&lt;p&gt;好了，废话不多说，开始正题！我们先写一个数组。&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;7&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;8&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;9&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;10&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;php comments&quot;&gt;//从数据库中取出的分类数据&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;php variable&quot;&gt;$original_array&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;= &lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;id&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; 1,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;pid&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; 0,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; &lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;新闻分类&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;),&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;id&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; 2,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;pid&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; 0,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; &lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;最新公告&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;),&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;id&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; 3,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;pid&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; 1,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; &lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;国内新闻&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;),&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;id&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; 4,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;pid&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; 1,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; &lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;国际新闻&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;),&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;id&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; 5,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;pid&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; 0,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; &lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;图片分类&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;),&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;id&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; 6,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;pid&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; 5,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; &lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;新闻图片&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;),&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php keyword&quot;&gt;array&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;id&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; 7,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;pid&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; 5,&lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;name&amp;#39;&lt;/code&gt; &lt;code class=&quot;php plain&quot;&gt;=&amp;gt; &lt;/code&gt;&lt;code class=&quot;php string&quot;&gt;&amp;#39;其它图片&amp;#39;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;)&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;);&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;同时，数据库是这个样子的。&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;https://files.jb51.net/file_images/article/201610/20161025113107096.png?2016925113118&quot; alt=&quot;&quot;/&gt;&lt;/p&gt;&lt;p&gt;说明：数据库的分类就是这个样子的！取出来的数组也是这个样子的！一般这样子的！&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.jb51.net/article/95645.htm#&quot; class=&quot;toolbar_item command_help help&quot;&gt;?&lt;/a&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;7&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;8&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;9&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;10&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;11&lt;/div&gt;&lt;div class=&quot;line number12 index11 alt1&quot;&gt;12&lt;/div&gt;&lt;div class=&quot;line number13 index12 alt2&quot;&gt;13&lt;/div&gt;&lt;div class=&quot;line number14 index13 alt1&quot;&gt;14&lt;/div&gt;&lt;div class=&quot;line number15 index14 alt2&quot;&gt;15&lt;/div&gt;&lt;div class=&quot;line number