iv class=&quot;line number71 index70 alt2&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;(&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number72 index71 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;)&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number73 index72 alt2&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number74 index73 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;)&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number75 index74 alt2&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number76 index75 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;)&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number77 index76 alt2&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number78 index77 alt1&quot;&gt;&lt;code class=&quot;php spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;php plain&quot;&gt;)&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number79 index78 alt2&quot;&gt;&amp;nbsp;&lt;/div&gt;&lt;div class=&quot;line number80 index79 alt1&quot;&gt;&lt;code class=&quot;php plain&quot;&gt;)&lt;/code&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','dev','2018-09-17 14:04:50','2018-11-09 15:33:50',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (12,'禅道php框架特点','&lt;p&gt;zentaoPHP框架的配置没有用什么ini文件，也没有用什么yaml文件，也没有用php的数组，而是直接用PHP对象的形式。这样配置文件不需要解析，就可以直接使用，而且引用起来十分的方便。形如$config-&amp;gt;db-&amp;gt;host的这种方式，而不是$config[&amp;#39;db&amp;#39;][&amp;#39;host&amp;#39;]的这种方式。书写起来会非常的流畅！&lt;/p&gt;&lt;p&gt;在zentaoPHP框架中，引用GET，POST, SERVER这样的全局变量，可以通过这样的方式：$this-&amp;gt;get-&amp;gt;userName, $this-&amp;gt;post-&amp;gt;userName, $this-&amp;gt;server-&amp;gt;remote_addr。 &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;','tool','2018-09-17 14:07:18','2018-11-09 15:34:19',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (13,'filezilla显示FTP已登录但读取目录列表失败的解决方法','&lt;ol style=&quot;list-style-type: decimal;&quot; class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;FTP一般有两个端口，21控制连接，20数据连接，查看防火墙， 21端口 20端口都放行&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;检查FileZilla传输模式是否设置为被动,如果是被动的话就要设置为主动， 反之亦然，可以在“文件”-“站点管理器”中设置&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;点击右边最后一个选项卡“字符集(CharSet)”，然后选择“强制UTF8(Force UTF8)” &amp;nbsp;--这条没有也行 &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;','dev','2018-09-17 14:09:01','2018-11-14 10:28:38',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (14,'gitlab几个重要文件','&lt;p&gt;如何查看 GitLab 版本号:cat /opt/gitlab/embedded/service/gitlab-rails/VERSION\r\n/var/opt/gitlab/nginx/conf/gitlab-http.conf 这个文件是gitlab内置的nginx的配置文件 有nginx真实监听端口号\r\n配置文件在/opt/gitlab/etc/gitlab.rb。这个文件用于gitlab如何调用80和8080的服务等 &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;','tool','2018-09-17 14:23:54','2018-11-09 15:35:33',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (15,'Linux查看版本的两个主要命令','&lt;p&gt;uname －a &amp;nbsp; （Linux查看版本当前操作系统内核信息）&lt;/p&gt;&lt;p&gt;cat /etc/issue 查看当前操作系统版本信息 如 Ubuntu 16.04.5 LTS \\n \\l&amp;nbsp;&amp;nbsp; 是我想要的\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;&lt;p&gt;其它还有&lt;/p&gt;&lt;p&gt;cat /etc/os-releaseruby&lt;/p&gt;&lt;p&gt;lsb_release –a&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&lt;/p&gt;','dev','2018-09-17 14:27:59','2018-11-12 15:16:00',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (16,'排查201机器上gitlab访问502错误的过程','&lt;p&gt;&amp;nbsp;命令行 docker logs gitlab &amp;gt;&amp;gt; tmp 再到tmp中查看 发现错误\r\n==&amp;gt; /var/log/gitlab/unicorn/current&lt;/p&gt;','service','2018-09-17 14:29:15','2018-11-12 16:03:09',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (17,'Windows中遇到不能创建 . 开头文件.htaccess','&lt;p&gt;先随便起个名字 到命令行如gitbash中mv 即可 重命名这个文件 &lt;br/&gt;&lt;/p&gt;&lt;p&gt;没有这个文件，会影响网站内部的链接 ，但是首页能访问 &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;','dev','2018-09-17 14:30:45','2018-11-09 15:36:53',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (18,'解决zentaophpBlog中的转义字符问题--后来改为用ueditor','&lt;p&gt;只要在模型类的create和update方法中将输入参数转换的specialchars函数去掉即可 &lt;br/&gt;&lt;/p&gt;&lt;p&gt;如果要将已经转为gt的&amp;gt;再转回来 在update方法中用上 $article-&amp;gt;content = html_entity_decode($article-&amp;gt;content); 再更新一次即可。 &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;','dev','2018-09-17 14:32:50','2018-11-09 15:37:52',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (21,'网站文件夹的可写目录不可忘记授权','&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;比如 upload tmp runtime 等 &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&lt;/p&gt;','dev','2018-11-06 18:56:05','2018-11-09 15:38:18',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (46,'.htaccess文件不能漏掉','&lt;p&gt;thinkphp和zentaophp都遇到过新部署到服务器的网页只能访问首页的问题，后来发现，除了要检查apache的rewrite模块是否开启，以及参数&amp;nbsp;&amp;lt;Directory /var/www/html&amp;gt;里的AllowOverride all以外，关键还要看scp上传时的.htaccess文件是否漏掉了，两次都是这样，要引起注意。&lt;br/&gt;&lt;br/&gt;&lt;/p&gt;','service','2018-11-12 14:06:58','2018-11-12 15:18:04',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (22,'解決ueditor在znetaophpBlog中的編輯和顯示問題---两天','&lt;p&gt;不用框架的input方法 改用post参数&amp;nbsp; 在create 和 upadte中都改&lt;br/&gt;&lt;/p&gt;&lt;p&gt;$article = \r\nfixer::input(&amp;#39;post&amp;#39;)-&amp;gt;specialchars(&amp;#39;title, \r\ncat_code,tag_id&amp;#39;)-&amp;gt;remove(&amp;#39;tag_id&amp;#39;)-&amp;gt;add(&amp;#39;date&amp;#39;, date(&amp;#39;Y-m-d \r\nH:i:s&amp;#39;))-&amp;gt;get();&lt;br/&gt;$article-&amp;gt;content=htmlspecialchars($_POST[&amp;#39;content&amp;#39;]);&lt;/p&gt;&lt;p&gt;并且 html中用&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;lt;script id=&amp;quot;content&amp;quot; name=&amp;quot;content&amp;quot; type=&amp;quot;text/plain&amp;quot;&amp;gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;lt;?php echo (htmlspecialchars_decode($article-&amp;gt;content));?&amp;gt;&lt;br/&gt;&amp;lt;/script&amp;gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; 要解码的&lt;/p&gt;&lt;p&gt;学到了函数&lt;br/&gt;&lt;/p&gt;&lt;h1&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;htmlspecialchars&amp;nbsp; ：把一些预定义的字符转换为 HTML 实体 框架的specialchars函数就是用了这个函数&lt;/span&gt;&lt;/h1&gt;&lt;h1&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;htmlspecialchars_decode() 把一些预定义的 HTML 实体转换为字符。这个函数框架只在过滤木马时用到 我在html显示中把它加上了&lt;/span&gt;&lt;br/&gt;&lt;/h1&gt;&lt;p&gt;&amp;nbsp;參考了鏈接 https://bbs.csdn.net/topics/392204502?t=1501469422859&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','dev','2018-11-06 18:57:59',NULL,1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (36,'uediter動態設置只讀屬性','&lt;p&gt;var ue = UE.getEditor(&amp;#39;content&amp;#39;,{readonly:true,});&amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;','tool','2018-11-08 11:23:40',NULL,1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (37,'裝sublime的語法檢查包---未完成','&lt;p&gt;在package controle中找install&amp;nbsp; ，輸入SublimeLinter 報錯找不到&lt;br/&gt;&lt;/p&gt;&lt;p&gt;到 &lt;a href=&quot;https://github.com/SublimeLinter/SublimeLinter&quot;&gt;https://github.com/SublimeLinter/SublimeLinter&lt;/a&gt; 下載&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt; &lt;/p&gt;','tool','2018-11-08 13:46:42','2018-11-12 16:03:47',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (38,'ubuntu查看軟件安裝位置-以sublime爲例','&lt;p&gt;&lt;span class=&quot;pre_description&quot;&gt;ubuntu软件安装位置\r\n &amp;nbsp; Ubuntu软件安装位置，一般都在/usr/bin下，个别可能会安装到/usr/share和/usr/local里&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;pre_description&quot;&gt;dpkg -L sublime-text-installer&amp;nbsp; &lt;br/&gt;/.&lt;br/&gt;/usr&lt;br/&gt;/usr/bin&lt;br/&gt;/usr/bin/subl&lt;br/&gt;/usr/share&lt;br/&gt;/usr/share/applications&lt;br/&gt;/usr/share/applications/sublime-text.desktop&lt;br/&gt;/usr/share/doc&lt;br/&gt;/usr/share/doc/sublime-text-installer&lt;br/&gt;/usr/share/doc/sublime-text-installer/copyright&lt;br/&gt;/usr/share/doc/sublime-text-installer/changelog.Debian.gz&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;pre_description&quot;&gt;到/usr/bin下 看subl可执行文件内容为&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;pre_description&quot;&gt;exec /opt/sublime_text/sublime_text &amp;quot;$@&amp;quot;&lt;br/&gt;被重定向到这里 就找到了/opt下&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;pre_description&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;','service','2018-11-08 13:59:21',NULL,1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (39,'手动安装sublime插件的方法','&lt;p&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;在ubuntu上安装了sublime text3之后，它的配置等信息在&lt;code style=&quot;font-family:&amp;#39;Source Code Pro&amp;#39;, monospace;color:rgb(199,37,78);background-color:rgb(249,242,244);&quot;&gt;~/.config/sublime-text-3&lt;/code&gt;目录，ls看一下里面有&lt;code style=&quot;font-family:&amp;#39;Source Code Pro&amp;#39;, monospace;color:rgb(199,37,78);background-color:rgb(249,242,244);&quot;&gt;Cache\r\n Installed Packages Local Packages&lt;/code&gt;这些目录&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;手动安装：&lt;br/&gt;可能由于各种原因，无法使用代码安装，那可以通过以下步骤手动安装Package Control：&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; 1.点击Preferences &amp;gt; Browse Packages菜单&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; 2.进入打开的目录的上层目录，然后再进入Installed Packages/目录&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n 3.下载 Package Control.sublime-package 并复制到Installed Packages/目录&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n //在http://www.imjeff.cn/blog/62/可以下载Package Control.sublime-package&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; 4.重启Sublime Text。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;参考&lt;a href=&quot;https://blog.csdn.net/u013401853/article/details/62238210&quot;&gt;https://blog.csdn.net/u013401853/article/details/62238210&lt;/a&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;和http://www.cnblogs.com/lwh-note/p/9339716.html&lt;/p&gt;','service','2018-11-08 14:24:09',NULL,1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (43,'zentaophp中新增lib类的调用','&lt;p&gt;1.在lib下新建文件夹，如mac，进入后新建文件，如mac.class.php&lt;br/&gt;&lt;/p&gt;&lt;p&gt;2.在控制类中调用 如&lt;/p&gt;&lt;p&gt;$this-&amp;gt;app-&amp;gt;loadClass(&amp;#39;mac&amp;#39;);&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $mac = new mac(PHP_OS);&lt;/p&gt;&lt;p&gt;再将值赋给视图，如$this-&amp;gt;view-&amp;gt;mac&amp;nbsp; = $mac-&amp;gt;macAddr;&lt;/p&gt;&lt;p&gt;详见下图：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181108/1541689118144461.png&quot; title=&quot;1541689118144461.png&quot; alt=&quot;image.png&quot; width=&quot;828&quot; height=&quot;244&quot;/&gt;&lt;/p&gt;','dev','2018-11-08 23:00:50','2018-11-09 16:57:50',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (42,'在zentaophp框架中使用返回值为array的方法--笨方法','&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; 在create.html中&lt;br/&gt;&lt;/p&gt;&lt;p&gt;$tag_arr=array();&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; foreach($tags as $key =&amp;gt; $value)&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; {&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $tag_arr[$key]= $value-&amp;gt;tag;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //$i=$i+1;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; }&lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; echo html::checkbox(&amp;#39;tag_id&amp;#39;, $tag_arr);&lt;/p&gt;&lt;p&gt;以及controle.php中&lt;br/&gt;&lt;/p&gt;&lt;p&gt;$tag_arr = array();&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; foreach($this-&amp;gt;blog-&amp;gt;getTagNameById($tag_id) as $key =&amp;gt; $value)&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; {&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; $tag_arr[$key]= $value-&amp;gt;tag;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; }&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $this-&amp;gt;view-&amp;gt;myBlogIndex&amp;nbsp;&amp;nbsp; = $this-&amp;gt;lang-&amp;gt;blog-&amp;gt;tagBy.&amp;#39;&amp;nbsp; &amp;#39;.$tag_arr[0].&amp;#39;&amp;nbsp; &amp;#39;.$this-&amp;gt;lang-&amp;gt;blog-&amp;gt;index;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;th style=&quot;text-align: center;&quot;&gt;id&lt;/th&gt;&lt;th style=&quot;text-align: center;&quot;&gt;content&lt;/th&gt;&lt;th style=&quot;text-align: center;&quot;&gt;使用场景&lt;/th&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;text-align: center;&quot;&gt;1&lt;/td&gt;&lt;td style=&quot;text-align: center;&quot;&gt;create.html&lt;/td&gt;&lt;td style=&quot;text-align: center;&quot;&gt;多个标签&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;text-align: center;&quot;&gt;2&lt;/td&gt;&lt;td style=&quot;text-align: center;&quot;&gt;edit.html&lt;/td&gt;&lt;td style=&quot;text-align: center;&quot;&gt;当前标签&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;','dev','2018-11-08 16:18:55','2018-11-09 16:57:59',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (44,'zentaophp 的orderby 函数','&lt;p&gt;zentaophp 的orderby 函数不能支持字段中的下划线 如create_time &lt;br/&gt;&lt;/p&gt;&lt;p&gt;因为被过滤掉了&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181109/1541734378987348.png&quot; title=&quot;1541734378987348.png&quot; alt=&quot;图片.png&quot; width=&quot;496&quot; height=&quot;293&quot;/&gt;&lt;br/&gt;&lt;/p&gt;','dev','2018-11-09 09:59:18','2018-11-10 14:03:41',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (45,'apache2: Could not reliably determine the server&amp;#039;s fully qualified domain name 错误','&lt;p&gt;AH00558: apache2: Could not reliably determine the server&amp;#39;s fully qualified domain name, using 172.17.0.7. Set the &amp;#39;ServerName&amp;#39; directive globally to suppress this message&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;$ sudo vim /etc/apache2/apache2.conf&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 最后加入一句: ServerName localhost:80&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','dev','2018-11-09 17:46:21',NULL,1,0);
