insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (51,'改linux命令行提示符','&lt;p class=&quot;MsoListParagraph&quot; style=&quot;margin-left: 56px;text-indent: 0&quot;&gt;&lt;span style=&quot;font-size:12px;font-family:宋体&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoListParagraph&quot; style=&quot;margin-left: 56px; text-align: left; text-indent: 0px;&quot;&gt;&lt;span style=&quot;font-size:12px;font-family:宋体&quot;&gt;vim /etc/profile&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoListParagraph&quot; style=&quot;margin-left: 56px; text-indent: 0px; text-align: center;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:12px;font-family: 宋体&quot;&gt;Export&amp;nbsp; PS1=&amp;#39;[\\u@\\h \\w]\\$&amp;#39;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:12px;font-family: 宋体&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: &amp;quot;Microsoft Yahei&amp;quot;, 微软雅黑, arial, 宋体, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;\\u &amp;nbsp; &amp;nbsp; 表示当前用户名&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: &amp;quot;Microsoft Yahei&amp;quot;, 微软雅黑, arial, 宋体, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;\\H &amp;nbsp; &amp;nbsp; 表示完整的主机名（\\h 表示短主机名，默认）&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: &amp;quot;Microsoft Yahei&amp;quot;, 微软雅黑, arial, 宋体, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;\\w &amp;nbsp; &amp;nbsp; 表示完整路径（\\W 表示短路径，默认)&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: &amp;quot;Microsoft Yahei&amp;quot;, 微软雅黑, arial, 宋体, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;\\$ &amp;nbsp; &amp;nbsp; 表示提示符（普通用户显示为字符$, root 用户显示为字符#）&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:12px;font-family: 宋体&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','service','2018-11-12 15:03:19','2018-11-12 16:05:14',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (52,'linux端口与服务相关','&lt;p class=&quot;MsoListParagraph&quot; style=&quot;margin-left:56px;text-align:left;text-indent:0;text-autospace: none&quot;&gt;&lt;span style=&quot;font-size:12px;font-family:宋体&quot;&gt;netstat -anpt | grep 80&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoListParagraph&quot; style=&quot;margin-left:56px;text-align:left;text-indent:0;text-autospace: none&quot;&gt;&lt;span style=&quot;font-size:12px;font-family:宋体&quot;&gt;Ubuntu &lt;/span&gt;&lt;span style=&quot;font-size:12px;font-family:宋体&quot;&gt;增加端口 ufw enable 80&lt;/span&gt;&lt;/p&gt;&lt;p&gt;Centos&lt;/p&gt;&lt;p class=&quot;MsoListParagraph&quot; style=&quot;margin-left:56px;text-align:left;text-indent:0;text-autospace: none&quot;&gt;&lt;span style=&quot;font-size:12px;font-family:宋体&quot;&gt;/etc/init.d/iptables status &lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;关闭打开端口&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:18px;font-family:华文楷体;color:#444444&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:18px;font-family:华文楷体;color:#444444&quot;&gt;iptables -A INPUT -p tcp --dport 445 -j DROP&lt;/span&gt;&lt;/p&gt;&lt;pre style=&quot;background:#F7F7F7;border:none;padding:0;padding:16px 16px 16px 16px&quot;&gt;$&amp;nbsp;iptables&amp;nbsp;-I&amp;nbsp;INPUT&amp;nbsp;-p&amp;nbsp;tcp&amp;nbsp;--dport&amp;nbsp;7777&amp;nbsp;-j&amp;nbsp;ACCEPT&lt;/pre&gt;&lt;pre style=&quot;background:#F7F7F7;border:none;padding:0;padding:16px 16px 16px 16px&quot;&gt;$&amp;nbsp;/etc/init.d/iptables&amp;nbsp;save&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#&amp;nbsp;保存&lt;/pre&gt;&lt;pre style=&quot;background:#F7F7F7;border:none;padding: 0;padding:16px 16px 16px 16px&quot;&gt;若报错&amp;nbsp;是因为没有配置过&amp;nbsp;service&amp;nbsp;iptables&amp;nbsp;save&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;查看所有服务&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:&amp;#39;Arial&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#666666;background:white&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:&amp;#39;Arial&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#666666;background:white&quot;&gt;service --status-all &lt;/span&gt;&lt;span style=&quot;;font-family:宋体;color:#666666;background:white&quot;&gt;（&lt;/span&gt;&lt;span style=&quot;;font-family:&amp;#39;Arial&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#666666;background:white&quot;&gt;ubuntu&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;color:#666666;background:white&quot;&gt;）&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;查看本机防火墙配置&lt;/span&gt; iptables -L -n&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:14px;font-family:宋体&quot;&gt;查看防火墙&amp;nbsp;&amp;nbsp;&lt;/span&gt;/etc/init.d/iptables status&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;/etc/rc.d/init.d/iptables --help&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;用法：&lt;/span&gt;iptables {start|stop|restart|condrestart|status|panic|save}&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;例如：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;/etc/init.d/iptables status&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;另有：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;sudo service iptables stop/status/start&lt;/p&gt;&lt;p&gt;chkconfig iptables on| off&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;改&lt;/span&gt;iptables&lt;span style=&quot;font-family:宋体&quot;&gt;并生效&lt;/span&gt;-109&lt;span style=&quot;font-family: 宋体&quot;&gt;为例：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:28px&quot;&gt;Vim /etc/sysconfig/iptables &lt;span style=&quot;font-family:宋体&quot;&gt;改为&lt;/span&gt;&lt;/p&gt;&lt;p&gt;:INPUT ACCEPT [0:0]&lt;/p&gt;&lt;p&gt;:FORWARD ACCEPT [0:0]&lt;/p&gt;&lt;p&gt;:OUTPUT ACCEPT [0:0]&lt;/p&gt;&lt;p&gt;-A INPUT -m state --state ESTABLISHED,RELATED -j ACCEPT&lt;/p&gt;&lt;p&gt;-A INPUT -p icmp -j ACCEPT&lt;/p&gt;&lt;p&gt;-A INPUT -i lo -j ACCEPT&lt;/p&gt;&lt;p&gt;-A INPUT -m state --state NEW -m tcp -p tcp --dport 22 -j ACCEPT&lt;/p&gt;&lt;p&gt;-A INPUT -m state --state NEW -m tcp -p tcp --dport 80 -j ACCEPT&lt;/p&gt;&lt;p&gt;-A INPUT -m state --state NEW -m tcp -p tcp --dport 83 -j ACCEPT&lt;/p&gt;&lt;p&gt;-A INPUT -m state --state NEW -m tcp -p tcp -s 124.205.50.106 --dport 5900:5905 -j ACCEPT&lt;/p&gt;&lt;p&gt;-A INPUT -j REJECT --reject-with icmp-host-prohibited&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;COMMIT&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:&amp;#39;Tahoma&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#333333;background:#EFEFEF&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;color:#2C2C2C;background:white&quot;&gt;service&amp;nbsp;iptables&amp;nbsp;restart&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;color:#2C2C2C;background:white&quot;&gt;/etc/rc.d/init.d/iptables save&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 13px;font-family:&amp;#39;Verdana&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#333333&quot;&gt;/etc/init.d/iptables restart&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;Ubuntu&lt;span style=&quot;font-family:宋体&quot;&gt;防火墙&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Simsun&amp;#39;,&amp;#39;serif&amp;#39;;color:#464646;background:#BCD3E5&quot;&gt;ufw enable&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Simsun&amp;#39;,&amp;#39;serif&amp;#39;;color:#464646;background: #BCD3E5&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Simsun&amp;#39;,&amp;#39;serif&amp;#39;;color:#464646;background:#BCD3E5&quot;&gt;ufw allow|deny [service][port]&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Simsun&amp;#39;,&amp;#39;serif&amp;#39;;color:#464646;background:#BCD3E5&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','service','2018-11-12 15:25:25','2018-11-12 16:01:42',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (53,'将virtualbox的虚拟机目录移动位置','&lt;h5&gt;一个最完美的办法，如下：&lt;/h5&gt;&lt;ol class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;菜单——管理——全局设定 ，更改 默认虚拟电脑位置。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;&lt;img class=&quot;&quot; src=&quot;//upload-images.jianshu.io/upload_images/1298765-836cc55c1926fb62.png?imageMogr2/auto-orient/strip%7CimageView2/2/w/522/format/webp&quot;/&gt;&lt;/p&gt;&lt;ol class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;复制 （移动）现有虚拟机目录到新位置，软件里删除现有虚拟机&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;菜单——控制——注册，逐个选择虚拟机目录里的 .vbox文件，导进虚拟机配置 。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;&lt;img class=&quot;&quot; src=&quot;//upload-images.jianshu.io/upload_images/1298765-2961a0a5fd9d6f26.png?imageMogr2/auto-orient/strip%7CimageView2/2/w/298/format/webp&quot;/&gt;&lt;/p&gt;&lt;p&gt;经测试，移动目录成功，而且原来备份的每个虚拟机的 系统快照 也都在。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;br/&gt;&lt;/p&gt;','service','2018-11-12 17:20:38',NULL,1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (54,'启动redmine命令--开发模式下','&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;83&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;机器上&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt; cd &amp;nbsp;/usr/local/redmine&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;执行启动&lt;/span&gt;&lt;span style=&quot;font-family: &amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;redmine &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;的命令：&lt;/span&gt;&lt;span style=&quot;font-family: &amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt; ruby script/rails server webrick –e development&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;Systemctl&amp;nbsp; stop firewalld &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;关闭防火墙&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;参考了&lt;/span&gt;&lt;span style=&quot;font-family: &amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;&lt;a href=&quot;https://blog.csdn.net/Honnyee/article/details/81535464%E7%9A%84firewalld%E5%91%BD%E4%BB%A4&quot;&gt;https://blog.csdn.net/Honnyee/article/details/81535464&lt;span style=&quot;font-family:宋体&quot;&gt;的&lt;/span&gt;firewalld&lt;span style=&quot;font-family:宋体&quot;&gt;命令&lt;/span&gt;&lt;/a&gt; &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;后来发现直接在虚拟机里关闭防火墙最简单&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;外面访问&lt;/span&gt;&lt;span style=&quot;font-family: &amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;3000&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;端口&lt;/span&gt; &lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;即可&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','service','2018-11-13 13:22:06',NULL,1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (55,'虚拟机扩容','&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;昨天看到&lt;/span&gt;82&lt;span style=&quot;font-family:宋体&quot;&gt;上的&lt;/span&gt;redmine&lt;span style=&quot;font-family:宋体&quot;&gt;虚拟机&lt;/span&gt;rxsunweb&lt;span style=&quot;font-family:宋体&quot;&gt;的空间不够&lt;/span&gt;&lt;/p&gt;&lt;p&gt;1.&lt;span style=&quot;font-family:宋体&quot;&gt;先关闭虚拟机&lt;/span&gt;&lt;/p&gt;&lt;p&gt;2.&lt;span style=&quot;font-family:宋体&quot;&gt;拷贝&lt;/span&gt;rxsunweb&lt;span style=&quot;font-family:宋体&quot;&gt;虚拟机&lt;/span&gt; &lt;span style=&quot;font-family: 宋体&quot;&gt;从&lt;/span&gt;C:\\Users\\Administrator\\VirtualBox VMs\\rxsunweb&lt;span style=&quot;font-family:宋体&quot;&gt;到&lt;/span&gt;D:\\virtualBox_redmine\\rxsunweb&lt;/p&gt;&lt;p&gt;3.&lt;span style=&quot;font-family:宋体&quot;&gt;到虚拟机的设置&lt;/span&gt;-&lt;span style=&quot;font-family:宋体&quot;&gt;存储&lt;/span&gt;-&lt;span style=&quot;font-family:宋体&quot;&gt;控制器中添加一个磁盘文件&lt;/span&gt; 100G &lt;span style=&quot;font-family:宋体&quot;&gt;，文件名为&lt;/span&gt;&lt;/p&gt;&lt;p&gt;D:\\virtualBox_redmine\\rxsunweb\\redmine_disk_2.vdi&lt;span style=&quot;font-family:宋体&quot;&gt;，&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;原来的&lt;/span&gt;D:\\virtualBox_redmine\\rxsunweb\\rxsunweb-disk2.vmdk &lt;span style=&quot;font-family:宋体&quot;&gt;只有&lt;/span&gt;30G&lt;span style=&quot;font-family:宋体&quot;&gt;左右&lt;/span&gt;&lt;/p&gt;&lt;p&gt;4.&lt;span style=&quot;font-family:宋体&quot;&gt;启动，进去看&lt;/span&gt;df –h&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &lt;img src=&quot;/ueditor/php/upload/image/20181113/1542088719299055.png&quot; title=&quot;1542088719299055.png&quot; alt=&quot;image.png&quot;/&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;5&lt;span style=&quot;font-family:宋体&quot;&gt;．&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;fdisk -l&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;查看新硬盘&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;/dev/sdb &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;已经识别到&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181113/1542088731614065.png&quot; title=&quot;1542088731614065.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;6.&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;对新硬盘分区&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;fdisk /dev/sdb&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;依次输入：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;n &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;新分区&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;p&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; #&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;选择分区类型&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;t &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;更改分区类型&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;Hex code (type L to list codes): 8e&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;w&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;保存分区结果&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;7.&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;让系统重新读取分区表：&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;# partprobe /dev/sdb&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;8.&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;创建物理卷（&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;PV&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;）&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt; # pvcreate /dev/sdb1&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;9.&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;查看物理卷信息：&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt; pvdisplay&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;10.&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;将&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;pv&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;加到&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;vg&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;中&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt; vgextend cl &amp;nbsp;/dev/sdb1&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;11.&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;将所有剩余空间全部划入&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;LV&lt;/span&gt; &amp;nbsp;lvextend -l +100%FREE /dev/mapper/cl-root&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;12.&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;最后一步&lt;/span&gt; &lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;关键是最后一步&lt;/span&gt; &lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;用&lt;/span&gt; &lt;span style=&quot;;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#4F4F4F;background:white&quot;&gt;xfs_growfs&lt;/span&gt;&amp;nbsp; &lt;span style=&quot;font-family: &amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;&amp;nbsp;/dev/mapper/cl-root&lt;/span&gt; &lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;然后&lt;/span&gt;&lt;span style=&quot;font-family: &amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;df –h &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;看到根目录空间已经变大&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:宋体;color:#4F4F4F;background:white&quot;&gt;以上虚拟机扩容及挂载&lt;/span&gt; &lt;span style=&quot;font-family:宋体;color:#4F4F4F;background:white&quot;&gt;参考了&lt;/span&gt;&lt;span style=&quot;font-family: &amp;#39;-apple-system&amp;#39;,&amp;#39;serif&amp;#39;;color:#4F4F4F;background:white&quot;&gt;&lt;a href=&quot;https://yq.aliyun.com/articles/486074&quot;&gt;https://yq.aliyun.com/articles/486074&lt;/a&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:14px&quot;&gt;&lt;span style=&quot;font-family:宋体;color:#3D464D;background:white&quot;&gt;最后一步参考了&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Helvetica&amp;#39;,&amp;#39;sans-serif&amp;#39;;color:#3D464D;background:white&quot;&gt;https://blog.csdn.net/qq_33932782/article/details/76612965&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','service','2018-11-13 13:23:32',NULL,1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (56,'Virtual box 屏幕截图方法','&lt;p&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;设备&lt;/span&gt;-&lt;span style=&quot;font-family: 宋体;&quot;&gt;视图&lt;/span&gt;-&lt;span style=&quot;font-family: 宋体;&quot;&gt;屏幕截图，会保存到物理主机的虚拟文件运行位置，即&lt;/span&gt;D:\\virtualBox_redmine\\rxsunweb&amp;nbsp; &lt;span style=&quot;font-family: 宋体;&quot;&gt;再从远程桌面拖拽到小本上&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','service','2018-11-13 13:24:16',NULL,1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (57,'网站部署后的文件同步问题','&lt;p&gt;84上传的文件.6上不能看到。&lt;/p&gt;&lt;p&gt;一直以来只关注数据库是否同步，却对文件上传的位置不关心。其实访问哪个服务器，文件就会上传到哪个服务器，换个服务器访问，文件没有同步过来，就不能看到，所以保持文件级别的数据自动同步是应纳入考虑范围的。&lt;/p&gt;&lt;p&gt;还有一个替代方案是在同一个服务器上，做出前端后端，保证文件永远在，更合理一些。&lt;br/&gt;&lt;/p&gt;','service','2018-11-13 14:56:59','2018-11-13 16:26:19',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (58,'ajax 提交之后,如何调试php','&lt;p&gt;&lt;span style=&quot;color: rgb(51, 51, 51); font-family: -apple-system, &amp;quot;Helvetica Neue&amp;quot;, Helvetica, Arial, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, &amp;quot;Microsoft Yahei&amp;quot;, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;chrome -&amp;gt;F12检查-&amp;gt;NetWork-&amp;gt;XHR&lt;/span&gt;，能看到回调函数的返回值&lt;/p&gt;','dev','2018-11-19 15:51:08',NULL,1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (59,' $getJSON注意事项','&lt;p&gt;&amp;nbsp;$.getJSON使用中，发现传入的参数到php中后多了一对单引号，导致拼sql的where条件时一直不能匹配，后采取&lt;/p&gt;&lt;p&gt;&amp;nbsp;$select_0 = substr($select_0,1,strlen($select_0)-2); 才去掉&lt;/p&gt;&lt;p&gt;代码在report控制器的show_table函数中&lt;/p&gt;','dev','2018-11-20 18:34:29',NULL,1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (60,'配置nfs服务器','&lt;p&gt;120.200配置nfs服务器&lt;/p&gt;&lt;p&gt;apt-get install rpcbind&lt;/p&gt;&lt;p&gt;apt-get install nfs-kernel-server&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;/etc/exports: the access control list for filesystems&lt;/p&gt;&lt;p&gt;加入一行&amp;nbsp;/home/public 192.168.200.0/24(rw) *ro保存&lt;/p&gt;&lt;p&gt;启动/etc/init.d/rpcbind start 无输出&lt;/p&gt;&lt;p&gt;再root@BIOS-git:/home# /etc/init.d/nfs-kernel-server start&lt;/p&gt;&lt;p&gt;&amp;nbsp;* Exporting directories for NFS kernel daemon...&lt;/p&gt;&lt;p&gt;exportfs: /etc/exports [1]: Neither &amp;#39;subtree_check&amp;#39; or &amp;#39;no_subtree_check&amp;#39; specified for export &amp;quot;192.168.200.0/24:/home/public&amp;quot;.&lt;/p&gt;&lt;p&gt;&amp;nbsp; Assuming default behaviour (&amp;#39;no_subtree_check&amp;#39;).&lt;/p&gt;&lt;p&gt;&amp;nbsp; NOTE: this default has changed since nfs-utils version 1.0.x&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;exportfs: No options for /home/public *ro: suggest *ro(sync) to avoid warning&lt;/p&gt;&lt;p&gt;exportfs: /etc/exports [1]: Neither &amp;#39;subtree_check&amp;#39; or &amp;#39;no_subtree_check&amp;#39; specified for export &amp;quot;*ro:/home/public&amp;quot;.&lt;/p&gt;&lt;p&gt;&amp;nbsp; Assuming default behaviour (&amp;#39;no_subtree_check&amp;#39;).&lt;/p&gt;&lt;p&gt;&amp;nbsp; NOTE: this default has changed since nfs-utils version 1.0.x&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;...done.&lt;/p&gt;&lt;p&gt;&amp;nbsp;* Starting NFS kernel daemon&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;...done.&lt;/p&gt;&lt;p&gt;大意是替我选了两个默认值&lt;/p&gt;&lt;p&gt;&amp;nbsp;netstat -antpl 查看rpcbind对应的111已启动&amp;nbsp; &amp;nbsp;tcp&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 0&amp;nbsp; &amp;nbsp; &amp;nbsp; 0 0.0.0.0:111&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;0.0.0.0:*&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;LISTEN&amp;nbsp; &amp;nbsp; &amp;nbsp; 9437/rpcbind&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;未果&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','service','2018-11-22 15:51:06','2018-11-22 18:12:50',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (61,'改禅道上传文件大小限制','&lt;p&gt;禅道上传文件大小限制写在www目录下的.htaccess文件中&lt;/p&gt;&lt;p&gt;php_value upload_max_filesize 100M&lt;/p&gt;','dev','2018-11-23 17:35:09',NULL,1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (62,'php.int文件中的时区和错误报告级别设置以及中文乱码','&lt;p&gt;1.时区：&lt;span style=&quot;color: rgb(79, 79, 79); font-family: -apple-system, &amp;quot;SF UI Text&amp;quot;, Arial, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, sans-serif; font-variant-ligatures: common-ligatures; background-color: rgb(255, 255, 255);&quot;&gt;在php.ini里面进行更改&lt;/span&gt;date.timezone =PRC 中国时区&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(79, 79, 79); font-family: -apple-system, &amp;quot;SF UI Text&amp;quot;, Arial, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, sans-serif; font-variant-ligatures: common-ligatures; background-color: rgb(255, 255, 255);&quot;&gt;2.错误报告级别：在php.ini里面进行更改error_reporting=E_ALL &amp;amp; ~E_NOTICE &amp;amp; ~E_DEPRECATED &amp;amp; ~E_WARNING;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(79, 79, 79); font-family: -apple-system, &amp;quot;SF UI Text&amp;quot;, Arial, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, sans-serif; font-variant-ligatures: common-ligatures; background-color: rgb(255, 255, 255);&quot;&gt;重启，不起作用；&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(79, 79, 79); font-family: -apple-system, &amp;quot;SF UI Text&amp;quot;, Arial, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, sans-serif; font-variant-ligatures: common-ligatures; background-color: rgb(255, 255, 255);&quot;&gt;到http.conf中加入&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;hljs-tag&quot; style=&quot;box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; font-size: 14px; word-wrap: break-word; font-family: &amp;quot;Source Code Pro&amp;quot;, &amp;quot;DejaVu Sans Mono&amp;quot;, &amp;quot;Ubuntu Mono&amp;quot;, &amp;quot;Anonymous Pro&amp;quot;, &amp;quot;Droid Sans Mono&amp;quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, sans-serif; font-variant-ligatures: common-ligatures; white-space: pre; color: rgb(0, 102, 102) !important;&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-title&quot; style=&quot;box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; color: rgb(79, 79, 79) !important; font-size: inherit; word-wrap: break-word;&quot;&gt;IfModule&lt;/span&gt; &lt;span class=&quot;hljs-attribute&quot; style=&quot;box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; color: rgb(79, 79, 79) !important; font-size: inherit; word-wrap: break-word;&quot;&gt;mod_php5.c&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;span style=&quot;color: rgb(79, 79, 79); font-family: &amp;quot;Source Code Pro&amp;quot;, &amp;quot;DejaVu Sans Mono&amp;quot;, &amp;quot;Ubuntu Mono&amp;quot;, &amp;quot;Anonymous Pro&amp;quot;, &amp;quot;Droid Sans Mono&amp;quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, sans-serif; font-size: 14px; font-variant-ligatures: common-ligatures; white-space: pre; background-color: rgb(246, 248, 250);&quot;&gt;\r\n &amp;nbsp; &amp;nbsp;php_admin_value error_reporting 24565&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;hljs-tag&quot; style=&quot;box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; font-size: 14px; word-wrap: break-word; font-family: &amp;quot;Source Code Pro&amp;quot;, &amp;quot;DejaVu Sans Mono&amp;quot;, &amp;quot;Ubuntu Mono&amp;quot;, &amp;quot;Anonymous Pro&amp;quot;, &amp;quot;Droid Sans Mono&amp;quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, sans-serif; font-variant-ligatures: common-ligatures; white-space: pre; color: rgb(0, 102, 102) !important;&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-title&quot; style=&quot;box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; color: rgb(79, 79, 79) !important; font-size: inherit; word-wrap: break-word;&quot;&gt;IfModule&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;hljs-tag&quot; style=&quot;box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; font-size: 14px; word-wrap: break-word; font-family: &amp;quot;Source Code Pro&amp;quot;, &amp;quot;DejaVu Sans Mono&amp;quot;, &amp;quot;Ubuntu Mono&amp;quot;, &amp;quot;Anonymous Pro&amp;quot;, &amp;quot;Droid Sans Mono&amp;quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, sans-serif; font-variant-ligatures: common-ligatures; white-space: pre; color: rgb(0, 102, 102) !important;&quot;&gt;it works。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;hljs-tag&quot; style=&quot;box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; font-size: 14px; word-wrap: break-word; font-family: &amp;quot;Source Code Pro&amp;quot;, &amp;quot;DejaVu Sans Mono&amp;quot;, &amp;quot;Ubuntu Mono&amp;quot;, &amp;quot;Anonymous Pro&amp;quot;, &amp;quot;Droid Sans Mono&amp;quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, sans-serif; font-variant-ligatures: common-ligatures; white-space: pre; color: rgb(0, 102, 102) !important;&quot;&gt;3.乱码需要三个步骤&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;hljs-tag&quot; style=&quot;box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; font-size: 14px; word-wrap: break-word; font-family: &amp;quot;Source Code Pro&amp;quot;, &amp;quot;DejaVu Sans Mono&amp;quot;, &amp;quot;Ubuntu Mono&amp;quot;, &amp;quot;Anonymous Pro&amp;quot;, &amp;quot;Droid Sans Mono&amp;quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, sans-serif; font-variant-ligatures: common-ligatures; white-space: pre; color: rgb(0, 102, 102) !important;&quot;&gt;首先在head中加入&amp;lt;meta http-equiv=&amp;quot;C&lt;/span&gt;&lt;span class=&quot;hljs-tag&quot; style=&quot;box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; font-size: 14px; word-wrap: break-word; font-family: &amp;quot;Source Code Pro&amp;quot;, &amp;quot;DejaVu Sans Mono&amp;quot;, &amp;quot;Ubuntu Mono&amp;quot;, &amp;quot;Anonymous Pro&amp;quot;, &amp;quot;Droid Sans Mono&amp;quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, sans-serif; font-variant-ligatures: common-ligatures; white-space: pre; color: rgb(0, 102, 102) !important;&quot;&gt;ontent-Type&amp;quot; content=&amp;quot;text/html; charset=utf8&amp;quot;&amp;gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;hljs-tag&quot; style=&quot;box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; font-size: 14px; word-wrap: break-word; font-family: &amp;quot;Source Code Pro&amp;quot;, &amp;quot;DejaVu Sans Mono&amp;quot;, &amp;quot;Ubuntu Mono&amp;quot;, &amp;quot;Anonymous Pro&amp;quot;, &amp;quot;Droid Sans Mono&amp;quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, sans-serif; font-variant-ligatures: common-ligatures; white-space: pre; color: rgb(0, 102, 102) !important;&quot;&gt;其次在UE中将文件保存为utf-8无bom头的格式&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;hljs-tag&quot; style=&quot;box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; font-size: 14px; word-wrap: break-word; font-family: &amp;quot;Source Code Pro&amp;quot;, &amp;quot;DejaVu Sans Mono&amp;quot;, &amp;quot;Ubuntu Mono&amp;quot;, &amp;quot;Anonymous Pro&amp;quot;, &amp;quot;Droid Sans Mono&amp;quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, sans-serif; font-variant-ligatures: common-ligatures; white-space: pre; color: rgb(0, 102, 102) !important;&quot;&gt;第三步 如果在zend studio中还是乱码，按常规”-&amp;gt;点击“工作空间”-&amp;gt;看右边-&amp;gt;选择“其他”-&amp;gt;下拉框选择&amp;quot;UTF-8&amp;quot;-&amp;gt;“确定”，默认的打开编码格式就是&amp;quot;UTF-8&amp;quot;格式的了&lt;/span&gt;&lt;/p&gt;','dev','2018-11-27 08:31:32','2018-11-27 11:02:32',1,0);
insert  into `blog`(`id`,`title`,`content`,`cat_code`,`create_time`,`update_time`,`is_show`,`is_delete`) values (63,'配置二级域名的方法','&lt;p&gt;以86机器为例：&lt;/p&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;list-style-type: decimal;&quot;&gt;&lt;li&gt;&lt;p&gt;在服务器的hosts文件中 (windows\\system32\\drivers\\etc\\hosts)中，写入本机对应的主机全名称，如86机器上的文件内容为&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;127.0.0.1&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;localhost&lt;/p&gt;&lt;p&gt;127.0.0.1&amp;nbsp; www.sunrx.com&lt;/p&gt;&lt;p&gt;127.0.0.1&amp;nbsp; test2.sunrx.com&lt;/p&gt;&lt;p&gt;127.0.0.1&amp;nbsp; thinkphp3.sunrx.com&lt;/p&gt;&lt;p&gt;127.0.0.1&amp;nbsp; oscshop2.sunrx.com&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;2.在apache的conf目录vhost文件中，写具体的路径配置，&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;lt;VirtualHost *:80&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; DocumentRoot &amp;quot;D:\\websites\\phpStudy\\PHPTutorial\\WWW&amp;quot;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; ServerName www.sunrx.com&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; ServerAlias www.sunrx.com&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;lt;Directory &amp;quot;D:\\websites\\phpStudy\\PHPTutorial\\WWW&amp;quot;&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;Options +Indexes +FollowSymLinks +ExecCGI&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; AllowOverride All&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; Order allow,deny&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; Allow from all&amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;lt;/Directory&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;lt;/VirtualHost&amp;gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;lt;VirtualHost *:80&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; DocumentRoot &amp;quot;D:\\websites\\phpStudy\\PHPTutorial\\WWW\\thinkphp&amp;quot;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; ServerName thinkphp3.sunrx.com&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; ServerAlias thinkphp3.sunrx.com&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;lt;Directory &amp;quot;D:\\websites\\phpStudy\\PHPTutorial\\WWW\\thinkphp&amp;quot;&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; Options +Indexes +FollowSymLinks +ExecCGI&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; AllowOverride All&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; Order allow,deny&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; Allow from all&amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;lt;/Directory&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;lt;/VirtualHost&amp;gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;lt;VirtualHost *:82&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; DocumentRoot &amp;quot;D:\\websites\\phpStudy\\PHPTutorial\\WWW\\thinkphp&amp;quot;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;lt;Directory &amp;quot;D:\\websites\\phpStudy\\PHPTutorial\\WWW\\thinkphp&amp;quot;&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; Options +Indexes +FollowSymLinks +ExecCGI&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; AllowOverride All&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; Order allow,deny&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; Allow from all&amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;lt;/Directory&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;lt;/VirtualHost&amp;gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;lt;VirtualHost *:80&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; DocumentRoot &amp;quot;D:\\websites\\phpStudy\\PHPTutorial\\WWW\\test2&amp;quot;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; ServerName test2.sunrx.com&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; ServerAlias test2.sunrx.com&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;lt;Directory &amp;quot;D:\\websites\\phpStudy\\PHPTutorial\\WWW\\test2&amp;quot;&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; Options&amp;nbsp; +Indexes +FollowSymLinks +ExecCGI&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; AllowOverride All&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; Order allow,deny&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; Allow from all&amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;lt;/Directory&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;lt;/VirtualHost&amp;gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;lt;VirtualHost *:80&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; DocumentRoot &amp;quot;D:\\websites\\phpStudy\\PHPTutorial\\WWW\\oscshop2&amp;quot;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; ServerName oscshop2.sunrx.com&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; ServerAlias oscshop2.sunrx.com&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;lt;Directory &amp;quot;D:\\websites\\phpStudy\\PHPTutorial\\WWW\\oscshop2&amp;quot;&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; Options&amp;nbsp; +Indexes +FollowSymLinks +ExecCGI&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; AllowOverride All&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; Order allow,deny&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; Allow from all&amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;lt;/Directory&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;lt;/VirtualHost&amp;gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;3. 在本地连接中加入局域网的域名解析服务器，这里是82&lt;/p&gt;&lt;p&gt;4.在局域网的域名解析服务器上，加入对地址的解析&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181206/1544075955143881.png&quot; title=&quot;1544075955143881.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;5浏览器输入thinkphp3.sunrx.com ,访问正确。&lt;/p&gt;','dev','2018-12-06 13:25:18',NULL,1,0);
